    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        // TODO add your handling code here:
        String username = tf2Username.getText();
        String password = tf2Password.getText();
        String passwordConfirm = tf2passwordconfirm.getText();
        String lastName = tf2LastName.getText();
        String mail = tf2Mail.getText();
        String name = tf2Name.getText();
        String celular = tf2Celular.getText();
        
        Conexion objConexion = new Conexion();
        
        usuariosBL oUsuarios = recuperarDatosGUI();
        
        String strSentencInsert = String.format("insert INTO persona(Id, UserName, Nombre, Apellido, Celular, Correo, Contraseña, ConfirContra) "
                + "values(null, '%s', '%s', '%s', '%s', '%s', '%s', '*******')", oUsuarios.getUsuario(), oUsuarios.getNombre(), oUsuarios.getApellido(), oUsuarios.getCelular(), oUsuarios.getCorreo(), oUsuarios.getContraseña());
        
        objConexion.ejecutarsentenciaSQL(strSentencInsert);
       
       
        
        if(password == null ? passwordConfirm == null  : password.equals(passwordConfirm)) {} 
        else{
            
            JOptionPane.showMessageDialog(this, "La contraseña debe de ser igual en ambos campos", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
        
        if(username.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar el Usuario", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
        if(name.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar el Nombre", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
        if(lastName.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar el Apellido", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
        
        if(celular.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar el Celular", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
         if(mail.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar el Correo", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
        if(password.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe ingresar la contraseña", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
        
        if(passwordConfirm.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Debe de confirmar la Contraseña", "Error", JOptionPane.ERROR_MESSAGE);
        } 
        
        
        if(passwordConfirm.isEmpty() || password.isEmpty() || mail.isEmpty() || celular.isEmpty() || lastName.isEmpty() || name.isEmpty() || username.isEmpty() ||  (password == null ? passwordConfirm != null : !password.equals(passwordConfirm)) )  {
           
        } else{
        FRMlogin vueltaLogin = new FRMlogin();
        
        vueltaLogin.setVisible(true) ;
        dispose();
                
        }
        //start the login process.
    }//GEN-LAST:event_btnRegisterActionPerformed

