    public usuariosBL recuperarDatosGUI() {
        usuariosBL oUsuarios = new usuariosBL();
        
        
        oUsuarios.setUsuario(tf2Username.getText());
        oUsuarios.setContraseña(tf2Password.getText());
        oUsuarios.setCorreo(tf2Mail.getText());
        oUsuarios.setCelular(tf2Celular.getText());
        oUsuarios.setNombre(tf2Name.getText());
        oUsuarios.setApellido(tf2LastName.getText());
       
        return oUsuarios;
    }
    
